import styled from "styled-components";

const Container = styled.div`
  display: flex;
  height: 1000px;
  width: 1000px;
  background-color: coral;
`;

const AppAward = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: Lime;
  figure {
    margin: 0;
    line-height: 0;

    position: relative;
    figcaption {
      position: absolute;
      top: 20em;
      text-align: center;
      color: white;
      line-height: 1;
      /* left: 0; */
      right: 0;
      bottom: 0;
    }
    img {
      height: 400px;
      /* width: 100px; */
      z-index: 1000;
    }
  }
`;

function Section() {
  return (
    <Container>
      <AppAward>
        <figure>
          <img src="./img/img-02-triple@3x.png" alt="" />
          <figcaption>2021년 12월 기준</figcaption>
        </figure>
      </AppAward>
    </Container>
  );
}

export default Section;
