import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  height: 46em;
  display: flex;
  justify-content: center;
  align-items: center;
  background-image: url("./img/img-01-bg.png");
  z-index: -1;
  background-repeat: no-repeat;
  background-size: contain;
`;
const MainLayer = styled.div`
  /* background-color: blueviolet; */
  box-sizing: border-box;
  position: absolute;
  top: 0px;
  width: 1040px;
  height: 1000px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 12em 7em;
  white-space: pre-wrap;
  button {
    background-color: beige;
    /* z-index: 10000; */
    /* align-self: flex-end; */
    justify-items: end;
    width: 180px;
    height: 50px;
    /* background-color: rgba(255, 255, 255, 0.1); */
  }
`;

const MainHeader = styled.div`
  display: flex;
  flex-direction: column;
`;

const MainSpan = styled.span`
  font-size: 4em;
  font-family: "Do Hyeon", sans-serif;
  color: whitesmoke;
  letter-spacing: 2px;
  height: 3em;
  /* background-color: aqua; */
  line-height: 1.2em;
  white-space: pre-wrap;
`;
const SubSpan = styled.span`
  font-size: 1em;
  color: whitesmoke;
  /* background-color: darkorange; */
  font-family: "Nanum Gothic Coding", monospace;
  padding-top: 1em;
`;

const SubLayer = styled.div`
  /* background-color: #fa1414; */
  box-sizing: border-box;
  position: absolute;
  top: 0px;
  /* width: 100%;
  height: 46em; */
  width: 1040px;
  height: 860px;
  display: flex;
  /* z-index: ; */
  display: flex;
  flex-direction: row-reverse;
  align-items: center;
`;
const SublayerPhone = styled.div`
  background-color: white;
  width: 330px;
  height: 680px;
  border-radius: 48px;
  left: 1em;
  display: flex;
  justify-content: center;
  z-index: 9;
  align-items: center;
  img {
    width: 300px;
    height: 650px;
    z-index: 10;
    background-repeat: no-repeat;
    background-size: contain;
  }
`;

function MainSection() {
  const mainText = "여행의 모든 것\n트리플로 한 번에";
  const subText = "예약부터 일정까지 앱 하나로 간편하게 준비하세요.";

  return (
    <Container>
      <MainLayer>
        <MainHeader>
          <MainSpan>{mainText}</MainSpan>
          <SubSpan>{subText}</SubSpan>
        </MainHeader>
        <button>앱 설치하기</button>
      </MainLayer>
      <SubLayer>
        <SublayerPhone>
          <img src="./img/img_01_screen.png" alt="main_phone_img" />
        </SublayerPhone>
      </SubLayer>
    </Container>
  );
}

export default MainSection;
