import styled, { css } from "styled-components";
import handleViewport from "react-in-viewport";
import {
  AnimationSlideUp,
  AnimationSlideDown,
  AnimationShow,
} from "./AnimationSlideUp";
import CountUp from "react-countup";

const Container = styled.div`
  display: flex;
  height: 552px;
  width: 100%;
  background-color: white;
  justify-content: center;
  align-items: center;
`;

const AppAward = styled.div`
  /* background-color: #ed1eed; */
  display: flex;
  width: 1040px;
  height: 412px;
  justify-content: center;
`;
const AwardImg = styled.div<{
  showUp: boolean;
}>`
  /* box-sizing: border-box; */
  display: flex;
  justify-content: center;
  align-items: center;
  height: 412px;
  width: 400px;
  /* background-color: Lime; */
  margin-right: 100px;
  ${({ showUp }) =>
    showUp === true &&
    css`
      opacity: 0;
      animation: ${AnimationSlideUp} 0.5s ease-in-out;
    `}
  ${({ showUp }) =>
    showUp === false &&
    css`
      opacity: 1;
      animation: none;
    `}
  figure {
    /* margin: 0; */
    line-height: 0;
    position: relative;
    figcaption {
      position: absolute;
      top: 280px;
      text-align: center;
      color: rgba(58, 58, 58, 0.7);
      /* line-height: 1; */
      left: 0;
      right: 0;
      bottom: 0;
    }
    img {
      height: 338px;
      width: 400px;
      /* z-index: 1000; */
    }
  }
`;

const AwardDescription = styled.div`
  /* background-color: lavender; */
  height: 412px;
  width: 460px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-left: 100px;
`;
const Description = styled.div<{
  showUp: boolean;
}>`
  /* background-color: blanchedalmond; */
  height: 220px;
  display: flex;
  align-items: center;
  li {
    font-size: 36px;
    letter-spacing: -1px;
    margin-bottom: 20px;
  }
  strong {
    font-weight: bold;
  }
  ${({ showUp }) =>
    showUp === true &&
    css`
      opacity: 0;
      animation: ${AnimationSlideUp} 0.5s ease-in-out 0.3s 1 forwards;
    `}
  ${({ showUp }) =>
    showUp === false &&
    css`
      opacity: 1;
      animation: none;
    `}
`;
const Award = styled.div<{
  showUp: boolean;
}>`
  /* background-color: cyan; */
  height: 160px;
  display: flex;
  justify-content: space-around;
  align-items: flex-start;
  color: rgba(58, 58, 58, 0.8);
  font-weight: bold;
  line-height: 22px;

  ${({ showUp }) =>
    showUp === true &&
    css`
      opacity: 0;
