import styled from "styled-components";
import Phone from "./Phone";
import { BrowserRouter } from "react-router-dom";
import {
  AnimationSlideUp,
  AnimationSlideDown,
  AnimationShow,
} from "./AnimationSlideUp";
import handleViewport from "react-in-viewport";
import { Component } from "react";

const StyledContainer = styled.div`
  position: relative;
  background-color: rgb(250, 250, 250);
  width: 100%;
  display: flex;
  justify-content: center;
  overflow: hidden;
`;
const StyledContents = styled.div`
  position: relative;
  /* background-color: black; */
  width: 1040px;
  height: 1000px;
  font-family: sans-serif;
  font-weight: normal;
  font-style: normal;
  color: rgb(58, 58, 58);
  text-align: center;
  margin: 0px;
`;
const StyledHeading = styled.h2`
  display: inline-block;
  /* background-color: beige; */
  position: relative;
  font-size: 52px;
  line-height: 68px;
  top: 140px;
  opacity: 0;
  animation: ${AnimationSlideUp} 0.8s ease-in-out 1 forwards;
`;

const StyledSubHeading = styled.div`
  /* background-color: blueviolet; */
  font-size: 17px;
  line-height: 30px;
  letter-spacing: -0.283333px;
  position: relative;
  top: 170px;
  opacity: 0;
  animation: ${AnimationSlideUp} 0.8s ease-in-out 0.08s 1 forwards;
`;

const StyledPhones = styled.div`
  position: absolute;
  bottom: -40px;
  width: 100%;
  /* background-color: coral; */
  display: flex;
  justify-content: center;
  & div {
    margin: 0px 10px;
    /* background-color: aqua; */
  }
  opacity: 0;
  animation: ${AnimationSlideUp} 0.8s ease-in-out 0.1s 1 forwards;
`;
let view = false;

class MySectionBlock extends Component {
  getStyle() {
    const { inViewport, enterCount } = this.props;
    //Fade in only the first time we enter the viewport
    if (inViewport && enterCount === 1) {
      return { WebkitTransition: "opacity 0.75s ease-in-out" };
    } else if (!inViewport && enterCount < 1) {
      return { WebkitTransition: "none", opacity: "0" };
    } else {
      return {};
    }
  }

  render() {
    const { enterCount, leaveCount } = this.props;
    return (
      <section>
        <div className="content" style={this.getStyle()}>
          <h1>Hello</h1>
          <p>{`Enter viewport: ${enterCount} times`}</p>
          <p>{`Leave viewport: ${leaveCount} times`}</p>
        </div>
      </section>
    );
  }
}

const Block = (props: { inViewport: boolean; forwardedRef: any }) => {
  const { inViewport, forwardedRef } = props;

  const color = inViewport ? "#217ac0" : "#ff9800";

  view = inViewport == true ? true : view;

  if (inViewport == true)
    return (
      <div className="viewport-block" ref={forwardedRef}>
        <StyledPhones>
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img-07-screen-1.png"}
          />
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img_07_screen_2.png"}
          />
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img_07_screen_3.png"}
          />
        </StyledPhones>
      </div>
    );
  else return <div className="viewport-block" ref={forwardedRef}></div>;
};

const ViewportBlock = handleViewport(Block /** options: {}, config: {} **/);

function SectionThree() {
  return (
    <StyledContainer>
      <StyledContents>
        <StyledHeading>
          모든 예약을
          <br />
          최저가로 한 번에!
        </StyledHeading>

        <StyledSubHeading>
          항공권부터 숙소, 투어·티켓까지 최저가로 예약.
          <br />
          트리플 전용 특가는 보너스.
        </StyledSubHeading>
        {/* <StyledPhones>
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img-07-screen-1.png"}
          />
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img_07_screen_2.png"}
          />
          <Phone
            width={290}
            height={598}
            imgSrc={"./img/img_07_screen_3.png"}
          />
        </StyledPhones> */}
        <ViewportBlock
          onEnterViewport={() => console.log("enter")}
          onLeaveViewport={() => console.log("leave")}
        />
      </StyledContents>
    </StyledContainer>
  );
}

export default SectionThree;
