import styled from "styled-components";

function MainSection() {
  const mainText = "여행의 모든 것\n트리플로 한 번에";
  const subText = "예약부터 일정까지 앱 하나로 간편하게 준비하세요.";

  return (
    <Container>
      <ContentsLayer>
        <MainLayer>
          <MainHeader>
            <MainTitle>{mainText}</MainTitle>
            <SubTitle>{subText}</SubTitle>
          </MainHeader>
          <button>앱 설치하기</button>
        </MainLayer>
        <SubLayer>
          <SubLayerPhone>
            <PhoneFrame />
            <img src="./img/img_01_screen.png" alt="main_phone_img" />
          </SubLayerPhone>
        </SubLayer>
      </ContentsLayer>
    </Container>
  );
}

const Container = styled.div`
  top: 0px;
  width: 100%;
  height: 860px;
  display: flex;
  justify-content: center;
  align-items: flex-end;

  background-image: url("./img/img-01-bg.png");
  background-repeat: no-repeat;
  background-size: 100% 100%;
`;

const ContentsLayer = styled.div`
  display: flex;
  /* background-color: blanchedalmond; */
  width: 1040px;
  height: 810px;
  margin-top: 50px;
  position: absolute;
  top: 0;
  flex-direction: row;
  padding: 12em 0px;
  justify-content: space-between;
  align-items: flex-start;
  overflow: hidden;
  /* visibility: hidden; */
  transition: opacity 1s ease-out;
  opacity: 0;
  /* height: 0; */
  &:hover {
    /* visibility: visible; */
    opacity: 1;
  }
`;

const MainLayer = styled.div`
  /* background-color: blueviolet; */
  height: 380px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  white-space: pre-wrap;
  button {
    justify-items: end;
    width: 180px;
    height: 50px;
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 30px;
    color: ${(props) => props.theme.lightBgTextColor};
    font-family: sans-serif;
    font-weight: bold;
    font-size: medium;
    border: 1px white solid;
    transition: 0.3s;
    &:hover {
      cursor: pointer;
      background-color: ${(props) => props.theme.lightBgColor};
      color: ${(props) => props.theme.colorBgTextColor};
    }
  }
`;

const MainHeader = styled.div`
  flex-direction: column;
  height: 246px;
`;

const MainTitle = styled.span`
  font-size: 5.5em;
  display: flex;
  font-family: sans-serif;
  font-weight: bold;
  color: ${(props) => props.theme.lightBgTextColor};
  letter-spacing: 0.8px;
  line-height: 1.1em;
  height: 2.5em;
  /* background-color: aqua; */
  white-space: pre-wrap;
`;
const SubTitle = styled.span`
  font-family: sans-serif;
  letter-spacing: 0px;
  font-weight: 500;
  color: ${(props) => props.theme.lightBgTextColor};
  /* background-color: darkorange; */
  padding-top: 1px;
`;

const SubLayer = styled.div`
  height: 800px;
  width: 330px;
  align-items: center;
  /* background-color: bisque; */
`;

const SubLayerPhone = styled.div`
  background-color: #fafafa;
  width: 330px;
  height: 680px;
  border-radius: 48px;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 1em;

  img {
    border-radius: 48px;
    width: 300px;
    height: 650px;
    background-repeat: no-repeat;
    background-size: contain;
  }
  box-shadow: rgb(0 0 0 / 18%) 0px 13px 24px 0px,
    rgb(0 0 0 / 8%) 0px -6px 9px 0px inset;
`;

const PhoneFrame = styled.div`
  background-color: #fafafa;
  /* background-color: aquamarine; */
  width: 162px;
  height: 38px;
  border-radius: 18px;
  position: absolute;
  right: 80px;
  top: 192px;
`;

export default MainSection;
