import styled, { css } from "styled-components";
import handleViewport from "react-in-viewport";
import {
  AnimationSlideUp,
  AnimationSlideDown,
  AnimationShow,
} from "./AnimationSlideUp";
import CountUp from "react-countup";

const Container = styled.div`
  display: flex;
  height: 552px;
  width: 100%;
  background-color: white;
  justify-content: center;
  align-items: center;
`;

const AppAward = styled.div`
  /* background-color: #ed1eed; */
  display: flex;
  width: 1040px;
  height: 412px;
  justify-content: center;
`;
const AwardImg = styled.div`
  /* box-sizing: border-box; */
  display: flex;
  justify-content: center;
  align-items: center;
  height: 412px;
  width: 400px;
  /* background-color: Lime; */
  margin-right: 100px;
  animation: ${AnimationSlideUp} 0.5s ease-in-out;
  figure {
    /* margin: 0; */
    line-height: 0;
    position: relative;
    figcaption {
      position: absolute;
      top: 280px;
      text-align: center;
      color: rgba(58, 58, 58, 0.7);
      /* line-height: 1; */
      left: 0;
      right: 0;
      bottom: 0;
    }
    img {
      height: 338px;
      width: 400px;
      /* z-index: 1000; */
    }
  }
`;

const AwardDescription = styled.div`
  /* background-color: lavender; */
  height: 412px;
  width: 460px;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  margin-left: 100px;
`;
const Description = styled.div`
  /* background-color: blanchedalmond; */
  height: 220px;
  display: flex;
  align-items: center;
  li {
    font-size: 36px;
    letter-spacing: -1px;
    margin-bottom: 20px;
  }
  strong {
    font-weight: bold;
  }
  opacity: 0;
  animation: ${AnimationSlideUp} 0.5s ease-in-out 0.3s 1 forwards;
`;
const Award = styled.div`
  /* background-color: cyan; */
  height: 160px;
  display: flex;
  justify-content: space-around;
  align-items: flex-start;
  color: rgba(58, 58, 58, 0.8);
  font-weight: bold;
  line-height: 22px;
  opacity: 0;
  animation: ${AnimationSlideUp} 0.5s ease-in-out 0.5s 1 forwards;

  div {
    display: flex;
    align-items: center;
  }
  img {
    margin-right: 10px;
    width: 54px;
    height: 54px;
  }
`;

const Block = (props: {
  inViewport: boolean;
  forwardedRef: any;
  enterCount: number;
}) => {
  const { inViewport, forwardedRef, enterCount } = props;
  if (inViewport && enterCount === 1) {
    return (
      <div ref={forwardedRef}>
        <div className="container"></div>
      </div>
    );
  }
  return (
    <div ref={forwardedRef}>
      <div className="container"></div>
    </div>
  );
};

function SectionTwo() {
